import fs from 'fs';

const imports = [
    'const path = require("path")',
    'const HtmlWebpackPlugin = require("html-webpack-plugin")',
    'const WebpackMd5Hash = require("webpack-md5-hash")',
    'const MiniCssExtractPlugin = require("mini-css-extract-plugin")'
];

const entry = `{
    main: './src/index.js',
}`

const output = `{
    path: path.resolve(__dirname, 'dist'),
    filename: '[name].[chunkhash].js' 
}`

export default (webpackFile) => {
    fs.readFile(__dirname + '/templates/webpack/config.js', 'utf-8', (err, data) => {
        data = data.replace('IMPORTS', imports.join('\n')).replace('ENTRY', entry).replace('OUTPUT', output);
        fs.writeFileSync('../../webpack.config.js', data);
        fs.writeFileSync(webpackFile, data);
    })
}
